public class Elseif2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		a=25;
		b=2020;
		
		if (a>=b)
		{
			System.out.println("A is graterthan B");
		} 
		
		else if (a>=10&&a<=25)
		{
			System.out.println( "A is lessthan B but it is in the range of 10 and 25");
		}
		
		else
		{
			System.out.println("A is lessthan B and also it is not in the range of 10 and 25");
			
		}
		
		}
		
		
		
		
	}


